sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    // Get the value of the state for closed state
    var stateValue = g_form.getValue('state');

    // Display or hide closure info based on state value
    if (stateValue === '3') {
        // Display closure info
        g_form.setSectionDisplay('closure_information', true);
    } else {
        // Hide closure info
        g_form.setSectionDisplay('closure_information', false);
    }

    // Type appropriate comment here, and begin script below

}
