sys_script_include.script:
var CloseRecord = Class.create();
CloseRecord.prototype = {
    initialize: function() {},
    close: function(recordID) {
        var record = new GlideRecord('x_110843_hr_human_resource');
        if (record.get(recordID)) {
            record.setValue('state', 3);
            record.update();
        }
    },
    type: 'CloseRecord'
};
