sys_ui_action.script:
// Define a function that resolves the record
function resolveRecord() {
    // Set the 'State' field to 'Resolved' by assigning an integer value
    g_form.setValue("state", 6); // 6 represents 'Resolved' state

    // Make the 'Comments' field mandatory 
    g_form.setMandatory("comments", true); // true sets the field to mandatory

    // Save the form to apply changes
    g_form.save(); // This triggers the save operation on the form

    // Call a server-side script using GlideAjax to perform additional server-side operations
    var ga = new GlideAjax("ResolveRecord"); // Create a new GlideAjax instance

    // Add a parameter with the unique ID of the current form (sys_id)
    ga.addParam("sys_id", g_form.getUniqueValue()); // Pass the record's sys_id

    // Specify the method in the server-side script to call
    ga.addParam("method_name", "resolve"); // This tells which method to call

    // Execute the GlideAjax call, triggering the server-side script
    ga.getXMLAnswer(); // This executes the call to the server-side script
}
